import React, { useEffect, useState } from "react";
import "./blurry-loading.css";

export default function BlurryLoading() {
  const [loadingPercentage, setLoadingPercentage] = useState(false);
  // const interval = setInterval(() => handlePercentageChange(), 30);
  let per = 0;

  const handlePercentageChange = () => {
    const bg_class = document.querySelector(".bg");
    const loading_class = document.querySelector(".loading-text");

    // setLoadingPercentage(loadingPercentage + 1);

    per++;
    bg_class.style.filter = `blur(${100 - per}px)`;
    loading_class.innerText = `${per}%`;
    loading_class.style.opacity = 1 - per / 100;
    if (per == 100) clearInterval(interval);
  };

  return (
    <div className="blurry-main-div">
      <section className="bg"></section>
      <div className="loading-text">{loadingPercentage}%</div>
    </div>
  );
}
