import React, { Component, useEffect, useState } from "react";
import "./progress-steps.css";

export default function ProgressSteps() {
  const circles = document.querySelectorAll(".circle");
  const progressContainer = document.getElementById("progress");
  const [progressWidth, setProgressWidth] = useState(5);

  const handleNextEvent = () => {
    for (const index in circles) {
      if (!circles[index].classList.contains("active")) {
        circles[index].classList.add("active");
        progressContainer.style.width = progressWidth + 30 + "%";
        break;
      }
    }
  };
  const handlePrevEvent = () => {
    let index = circles.length - 1;

    while (index > 0) {
      if (circles[index].classList.contains("active")) {
        circles[index].classList.remove("active");
        progressContainer.style.width = progressContainer.style.width - 30;
        break;
      }
      index -= 1;
    }
  };

  useEffect(() => {
    // circles.map((circle) => {});
  }, []);

  return (
    <div className="mainBody">
      <div className="mainDiv">
        <div className="progress-container">
          <div className="progress" id="progress"></div>
          <div className="circle active">1</div>
          <div className="circle">2</div>
          <div className="circle">3</div>
          <div className="circle">4</div>
        </div>
        <button
          className="btn"
          id="prev"
          disabled={true}
          onClick={() => handlePrevEvent()}
        >
          Prev
        </button>
        <button className="btn" id="prev" onClick={() => handleNextEvent()}>
          Next
        </button>
      </div>
    </div>
  );
}
